contract SpyritRealmWL is ERC1155, Ownable {

    bool public mintActive = true;

    uint256 public MAX_PER_WALLET = 1;
    uint256 public cost = 30 ether;
    uint256 public MAX_ID = 4;

    string public name = "SpyritRealmWL";
    string public symbol = "SPYR";

    mapping(address => bool) public whiteListed;
    mapping(address => uint256[]) public tokenIds;
    mapping(address => uint256) public tokenIdsBalance;

    constructor() ERC1155("https://ipfs.io/ipfs/QmTmjVwvscj3onUYNWp82xsRY622QeA8ikJFoPFip9q5rH/{id}.json", 0xCA63D9208E3a40E6Ff0eAfFDcf01daf885612CF3) {
        whiteListed[msg.sender] = true;
    }

    function mint(uint256 id) public payable {
        uint256 balance = balanceOf(msg.sender, id);

        require( mintActive, "Mint hasn't started" );
        require( whiteListed[msg.sender], "User is not whitelisted" );
        require( balance + 1 <= MAX_PER_WALLET, "Can't mint over MAX_PER_WALLET");
        require( id <= MAX_ID, "ID is over MAX_ID");

        require(msg.value >= cost);

        _mint(msg.sender, id, 1, "");
        tokenIds[msg.sender].push(id);
        tokenIdsBalance[msg.sender] += 1;
    }

    function enableMint(bool _enable) public onlyOwner {
        mintActive = _enable;
    }

    function updateMaxPerWallet(uint256 _maxPerWallet) public onlyOwner {
        MAX_PER_WALLET = _maxPerWallet;
    }

    function uri(uint256 _tokenId) override public pure returns (string memory) {
        return string(
            abi.encodePacked(
            "https://ipfs.io/ipfs/QmTmjVwvscj3onUYNWp82xsRY622QeA8ikJFoPFip9q5rH/",
            Strings.toString(_tokenId),
            ".json"
           )
        );
    }

    function setWhitelist(address[] memory addresses, bool value) public onlyOwner{
        for (uint i = 0; i < addresses.length; i++) {
            whiteListed[addresses[i]] = value;
        }
    }

    function updateMaxID(uint256 _maxID) public onlyOwner {
        MAX_ID = _maxID;
    }

    function updateMintCost(uint256 _newPrice) public onlyOwner {
        cost = _newPrice;
    }

    function withdraw() public payable onlyOwner {
        require(payable(msg.sender).send(address(this).balance));
    }
}

